// DOM elements
const taskModal = document.getElementById('task-modal');
const addTaskButton = document.getElementById('add-task');
const closeModalButton = document.getElementById('close-modal');
const saveTaskButton = document.getElementById('save-task');
const taskList = document.querySelector('#task-list ul');
const saveNoteButton = document.getElementById('save-note');
const noteInput = document.getElementById('note-input');
const notesList = document.getElementById('notes-list');
const editClientButton = document.getElementById('edit-client');

// Show modal when clicking on 'Add Task'
addTaskButton.addEventListener('click', () => {
    taskModal.style.display = 'flex';
});

// Close modal
closeModalButton.addEventListener('click', () => {
    taskModal.style.display = 'none';
});

// Save task
saveTaskButton.addEventListener('click', () => {
    const taskName = document.getElementById('task-name').value;
    const taskDeadline = document.getElementById('task-deadline').value;
    const taskPriority = document.getElementById('task-priority').value;
    const taskStatus = document.getElementById('task-status').value;

    // Validate that task name is not empty
    if (!taskName.trim()) {
        alert('Please enter a task name.');
        return;
    }

    const newTask = document.createElement('li');
    newTask.innerHTML = 
        <span>${taskName} - ${taskDeadline} - <strong>${taskPriority}</strong></span>
        <span class="task-status ${taskStatus}">${taskStatus.charAt(0).toUpperCase() + taskStatus.slice(1)}</span>
        <button class="task-remove-btn">Remove</button>
    ;
    taskList.appendChild(newTask);

    // Reset the form and close modal
    document.getElementById('task-name').value = '';
    document.getElementById('task-deadline').value = '';
    document.getElementById('task-priority').value = 'low';
    document.getElementById('task-status').value = 'pending';
    taskModal.style.display = 'none';
});

// Remove task
taskList.addEventListener('click', (event) => {
    if (event.target.classList.contains('task-remove-btn')) {
        event.target.parentElement.remove();
    }
});

// Save note functionality (localStorage or backend integration could be added)
saveNoteButton.addEventListener('click', () => {
    const note = noteInput.value;
    if (note.trim()) {
        // Create a new note element
        const noteElement = document.createElement('div');
        noteElement.classList.add('note-item');
        noteElement.innerHTML = 
            <p>${note}</p>
            <button class="edit-note-btn">Edit</button>
            <button class="delete-note-btn">Delete</button>
        ;
        notesList.appendChild(noteElement);

        // Clear input
        noteInput.value = '';
    } else {
        alert('Please enter a note.');
    }
});

// Edit note functionality (you can add a better editing functionality)
notesList.addEventListener('click', (event) => {
    if (event.target.classList.contains('edit-note-btn')) {
        const noteItem = event.target.parentElement;
        const noteText = noteItem.querySelector('p').textContent;
        const newNoteText = prompt('Edit your note:', noteText);
        if (newNoteText !== null && newNoteText.trim()) {
            noteItem.querySelector('p').textContent = newNoteText;
        }
    }
});

// Delete note functionality
notesList.addEventListener('click', (event) => {
    if (event.target.classList.contains('delete-note-btn')) {
        event.target.parentElement.remove();
    }
});

// Edit client button (example behavior)
editClientButton.addEventListener('click', () => {
    alert('Edit Client Info functionality will be implemented!');
});

