// Ensure you're querying the correct parent element for sticky notes
let stickyNotesContainer = root_element.querySelector('#sticky-notes-container');
let addNoteButton = root_element.querySelector('#add-note-button');

// Function to fetch sticky notes from the database
async function fetchStickyNotes() {
    try {
        const response = await fetch('/api/method/get_sticky_notes', { method: 'GET' });
        if (!response.ok) throw new Error(`HTTP error! Status: ${response.status}`);
        const data = await response.json();
        displayStickyNotes(data.notes || []);
    } catch (error) {
        console.error("Error fetching sticky notes:", error);
    }
}

// Function to display sticky notes
function displayStickyNotes(notes) {
    stickyNotesContainer.innerHTML = '';
    notes.forEach(note => createStickyNoteElement(note.id, note.content));
}

// Function to create a new sticky note
function createStickyNoteElement(id, content = '') {
    const note = document.createElement('div');
    note.className = 'sticky-note';
    note.dataset.id = id;

    const textarea = document.createElement('textarea');
    textarea.value = content;
    textarea.addEventListener('input', () => saveStickyNoteContent(id, textarea.value));

    const deleteButton = document.createElement('button');
    deleteButton.textContent = 'Delete';
    deleteButton.addEventListener('click', () => deleteStickyNote(id, note));

    note.appendChild(textarea);
    note.appendChild(deleteButton);
    stickyNotesContainer.appendChild(note);
}

// Function to save sticky note content
async function saveStickyNoteContent(id, content) {
    try {
        await fetch('/api/method/save_sticky_note', {
            method: 'POST',
            headers: { 'Content-Type': 'application/json' },
            body: JSON.stringify({ id, content })
        });
    } catch (error) {
        console.error("Error saving sticky note:", error);
    }
}

// Function to delete a sticky note
async function deleteStickyNote(id, noteElement) {
    try {
        await fetch(`/api/method/delete_sticky_note?id=${id}`, { method: 'DELETE' });
        stickyNotesContainer.removeChild(noteElement);
    } catch (error) {
        console.error("Error deleting sticky note:", error);
    }
}

// Event listener for adding a new note
addNoteButton.addEventListener('click', async () => {
    try {
        const response = await fetch('/api/method/create_sticky_note', { method: 'POST' });
        const newNote = await response.json();
        createStickyNoteElement(newNote.id, newNote.content);
    } catch (error) {
        console.error("Error creating a new sticky note:", error);
    }
});

// Initial fetch of sticky notes
fetchStickyNotes();

