frappe.ready(() => {
    const doctype = "CRM Lead"; // Replace with the actual Doctype
    const kanban_name = "Kanban"; // Replace with the actual Kanban view name

    frappe.call({
      method: "frappe.desk.kanban.get_kanban_board",
      args: { kanban_board: kanban_name },
      callback: function (r) {
        if (r.message) {
          const kanban_config = r.message;
          renderKanbanView(kanban_config, doctype);
        } else {
          console.error("Failed to load Kanban board");
        }
      },
    });

    function renderKanbanView(kanban_config, doctype) {
      frappe.views.KanbanBoard({
        doctype: doctype,
        kanban_board: kanban_config,
        container: $("#kanban-view-container"),
      });
    }
  });

